xquery version "1.0-ml";
import module namespace admin = "http://marklogic.com/xdmp/admin" at "/MarkLogic/admin.xqy";
let $config := admin:get-configuration() return admin:database-get-range-element-indexes($config, xdmp:database("Documents") )
=> The xml for any element range indexes in the "Documents" database, for example: <range-element-index xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xmlns="http://marklogic.com/xdmp/database"> <scalar-type>date</scalar-type> <collation/> <namespace-uri>mynamespace</namespace-uri> <localname>myelementname</localname> <range-value-positions>false</range-value-positions> </range-element-index>

  xquery version "1.0-ml";

  import module namespace admin = "http://marklogic.com/xdmp/admin"
	         at "/MarkLogic/admin.xqy";

  let $config := admin:get-configuration()
  let $spec := admin:forest-set-enabled($config,
    xdmp:forest("myForest"), fn:true() )
  return
  admin:save-configuration-without-restart($spec)

  xquery version "1.0-ml";

  import module namespace admin = "http://marklogic.com/xdmp/admin"
