let $query := cts:and-not-query(
                 cts:word-query("wanted"),
                 cts:word-query("unwanted"))
return cts:and-not-query-positive-query($query)

  => cts:word-query("wanted", (), 1)

let $query := cts:and-not-query(
                 cts:word-query("wanted"),
                 cts:word-query("unwanted"))
return cts:and-not-query-negative-query($query)

  => cts:word-query("unwanted", (), 1)

(: create a document with test data :) xdmp:document-insert("/dates.xml", <root> <entry> <date>2007-01-01</date> <info>Some information.</info> </entry> <entry> <date>2006-06-23</date> <info>Some other information.</info> </entry> <entry> <date>1971-12-23</date> <info>Some different information.</info> </entry> </root>);
(: requires an element (range) index of type xs:date on "date" :) cts:search(doc("/dates.xml")/root/entry, cts:element-range-query(xs:QName("date"), "<=", xs:date("2000-01-01"))) (: returns the following node: <entry> <date>1971-12-23</date> <info>Some different information.</info> </entry> :) ; (: requires an element (range) index of type xs:date on "date" :) cts:search(doc("/dates.xml")/root/entry, cts:and-query(( cts:element-range-query(xs:QName("date"), ">", xs:date("2006-01-01")), cts:element-range-query(xs:QName("date"), "<", xs:date("2008-01-01"))))) (: returns the following 2 nodes: <entry> <date>2007-01-01</date> <info>Some information.</info> </entry>
<entry> <date>2006-06-23</date> <info>Some other information.</info> </entry> :)

(: Insert few documents with test data :) let $content1 := <name><fname>John</fname><mname>Rob</mname><lname>Goldings</lname></name> let $content2 := <name><fname>Jim</fname><mname>Ken</mname><lname>Kurla</lname></name> let $content3 := <name><fname>Ooi</fname><mname>Ben</mname><lname>Fu</lname></name> let $content4 := <name><fname>James</fname><mname>Rick</mname><lname>Tod</lname></name> return ( xdmp:document-insert("/aname1.xml",$content1), xdmp:document-insert("/aname2.xml",$content2), xdmp:document-insert("/aname3.xml",$content3), xdmp:document-insert("/aname4.xml",$content4));
(: requires a field (range) index of type xs:string on field "aname" :)
(: returns the following: <?xml version="1.0" encoding="UTF-8"?> <name><fname>John</fname><mname>Rob</mname><lname>Goldings</lname></name> <?xml version="1.0" encoding="UTF-8"?> <name><fname>Ooi</fname><mname>Ben</mname><lname>Fu</lname></name> :) ; (: requires an element (range) index of type xs:string on "aname" :) cts:contains(doc(),cts:field-range-query("aname",">","Jim Kurla")) (: returns "true". :)

