	         at "/MarkLogic/cpf/pipelines.xqy";

  dom:add-pipeline( "Incoming",
	         p:get("Entity Extraction")/p:pipeline-id )

  xquery version "1.0-ml";
  import module namespace dom = "http://marklogic.com/cpf/domains"
	         at "/MarkLogic/cpf/domains.xqy";
