return
fn:boolean($x[0])
=> returns false.

let $x := ("a", "b", "c")

fn:insert-before($x, 0, "z") returns ("z", "a", "b", "c")

fn:insert-before($x, 1, "z") returns ("z", "a", "b", "c")

fn:insert-before($x, 2, "z") returns ("a", "z", "b", "c")

fn:insert-before($x, 3, "z") returns ("a", "b", "z", "c")

fn:insert-before($x, 4, "z") returns ("a", "b", "c", "z")

let $x := ("a", "b", "c")

fn:remove($x, 0) returns ("a", "b", "c")

fn:remove($x, 1) returns ("b", "c")

fn:remove($x, 6) returns ("a", "b", "c")

fn:remove((), 3) returns ()

let $x := ("a", "b", "c")
return
fn:reverse($x)

=> ("c", "b", "a")

Assume $seq = ($item1, $item2, $item3, $item4, ...)

fn:subsequence($seq, 4) returns ($item4, ...)

fn:subsequence($seq, 3, 2) returns ($item3, $item4)

fn:zero-or-one("hello")

=> "hello"

fn:zero-or-one(("hello", "goodbye"))

=> XDMP-MORETHANONEITEM exception (because there are two items)

fn:one-or-more( () )

=> XDMP-ZEROITEMS exception

fn:one-or-more("hello")

=> "hello"

fn:exactly-one(("hello"))

=> "hello"

fn:exactly-one(("hello", "goodbye"))

=> XDMP-NOTONEITEM exception (because there are 2 items)

summary.pre:
if (every $v in $c satisfies $c[1] ge $v)
then $c[1]
else fn:max(fn:subsequence($c, 2))

if (every $v in $c satisfies $c[1] le $v)
then $c[1]
else fn:min(fn:subsequence($c, 2))

function.example:
let $x := document{
  <html xmlns="http://www.w3.org/1999/xhtml">
    <p id="myID">hello</p>
  </html> }
return
fn:id("myID", $x)

=> <p id="myID" xmlns="http://www.w3.org/1999/xhtml">hello</p>

xquery version "1.0-ml";
declare namespace xh="http://www.w3.org/1999/xhtml";

let $x := document {
  <html xmlns="http://www.w3.org/1999/xhtml">
    <p id="myID">hello</p>
    <p>hello</p>
  </html> }
return
$x/xh:html/xh:p[. is fn:id("myID")]

=> <p id="myID" xmlns="http://www.w3.org/1999/xhtml">hello</p>

xquery version "1.0-ml"; (: This module should be at the App Server root with a name test.xqy, so it resolves to /test.xqy from the App Server's root. :) xdmp:set-response-content-type("text/plain"),
xdmp:document-insert("testing.xml", <foo/>); xdmp:document-insert("/testing.xml", <bar/>);
declare option xdmp:output "indent-untyped=yes";
<root> <document-from-module> <document>{fn:document("testing.xml")}</document> </document-from-module> <document-from-eval> <document>{xdmp:eval('fn:document("testing.xml")')}</document> </document-from-eval> </root>
(: From the module, the document cannot find the URI "testing.xml" because the base-uri of the module is its URI relative to the App Server root. It therefore finds /testing.xml because that is what the URI testing.xml resolves to (testing.xml resolves to /testing.xml when the base-uri is /test.xqy). From an eval, there is no base-uri, so it can find an unrooted URI (testing.xml resolves to testing.xml when the base-uri is empty). :)
=> <root> <document-from-module> <document> <bar/> </document> </document-from-module> <document-from-eval> <document> <foo/> </document> </document-from-eval> </root>

