xquery version "1.0-ml";
(: this example requires a document named hello.xsl directly at the App Server root with the following content:
<xsl:stylesheet xmlns:xsl="http://www.w3.org/1999/XSL/Transform" version="2.0"> <xsl:template match="/"> <xsl:text>hello</xsl:text> </xsl:template> </xsl:stylesheet> :) xdmp:xslt-invoke("/hello.xsl", document{ <foo/> }) => hello

xquery version "1.0-ml" ;
(: Hello World example for xslt:invoke, with a parameter. Assumes a stylesheet names params.xsl directly at the App Server root with the following content: <xsl:stylesheet xmlns:xsl="http://www.w3.org/1999/XSL/Transform" xmlns:f="foo" xmlns:b="bar" version="2.0"> <xsl:param name="f:pName"/> <xsl:param name="b:bName"/> <xsl:param name="cName"/> <xsl:param name="greeting" select="'Hi there '"/> <xsl:template match="/"> <output> <xsl:copy-of select="node"/> <greeting><xsl:value-of select="$greeting"/></greeting> <param><xsl:value-of select="$f:pName"/></param> <param><xsl:value-of select="$b:bName"/></param> <param><xsl:value-of select="$cName"/></param> </output> </xsl:template> </xsl:stylesheet> :)
let $params := map:map() let $_put := map:put( $params, xdmp:key-from-QName(fn:QName("foo", "pName")), "Stephen") let $_put := map:put( $params, xdmp:key-from-QName(fn:QName("bar", "bName")), "Ron") let $_put := map:put( $params, "cName", "Dave") return xdmp:xslt-invoke("/params.xsl", document { <node>Hello World</node> }, $params) => <?xml version="1.0" encoding="ASCII"?> <output xmlns:f="foo" xmlns:b="bar"> <node>Hello World</node> <greeting>Hi there </greeting> <param>Stephen</param> <param>Ron</param> <param>Dave</param> </output>

xquery version "1.0-ml" ;
(: Hello World example for xslt:eval, with a parameter :)
let $params := map:map() let $_put := map:put( $params, xdmp:key-from-QName(fn:QName("foo", "pName")), "Stephen") let $_put := map:put( $params, xdmp:key-from-QName(fn:QName("bar", "bName")), "Ron") let $_put := map:put( $params, "cName", "Dave") return xdmp:xslt-eval( <xsl:stylesheet xmlns:xsl="http://www.w3.org/1999/XSL/Transform" xmlns:f="foo" xmlns:b="bar" version="2.0"> <xsl:param name="f:pName"/> <xsl:param name="b:bName"/> <xsl:param name="cName"/> <xsl:param name="greeting" select="'Hi there '"/> <xsl:template match="/"> <output> <xsl:copy-of select="node"/> <greeting><xsl:value-of select="$greeting"/></greeting> <param><xsl:value-of select="$f:pName"/></param> <param><xsl:value-of select="$b:bName"/></param> <param><xsl:value-of select="$cName"/></param> </output> </xsl:template> </xsl:stylesheet>, document { <node>Hello World</node> }, $params) => <?xml version="1.0" encoding="ASCII"?> <output xmlns:f="foo" xmlns:b="bar"> <node>Hello World</node> <greeting>Hi there </greeting> <param>Stephen</param> <param>Ron</param> <param>Dave</param> </output>

